import React, { useEffect } from 'react';
import { TokenStorage } from '../utils/tokenStorage';

const OAuth2Redirect = ({ onLoginSuccess }) => {
  useEffect(() => {
    // üîí URL ÌååÎùºÎØ∏ÌÑ∞ÏóêÏÑú ÏÑ±Í≥µ Ïó¨Î∂ÄÎßå ÌôïÏù∏ (ÌÜ†ÌÅ∞ÏùÄ Îçî Ïù¥ÏÉÅ URLÏóê ÏóÜÏùå)
    const urlParams = new URLSearchParams(window.location.search);
    const success = urlParams.get('success');

    if (success === 'true') {
      // üîí HTTP-Only Ïø†ÌÇ§Ïóê ÌÜ†ÌÅ∞Ïù¥ ÏûêÎèôÏúºÎ°ú ÏÑ§Ï†ïÎêòÏñ¥ ÏûàÏùå
      // ÌÜ†ÌÅ∞ Ï°¥Ïû¨ Ïó¨Î∂Ä ÌôïÏù∏ÏùÑ ÏúÑÌï¥ API Ìò∏Ï∂úÎ°ú Í≤ÄÏ¶ù
      fetch('/api/auth/verify', {
        method: 'GET',
        credentials: 'include' // Ïø†ÌÇ§ Ìè¨Ìï®
      })
      .then(response => {
        if (response.ok) {
          console.log('OAuth2 Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ, Ïø†ÌÇ§ Í∏∞Î∞ò Ïù∏Ï¶ù ÏÑ§Ï†ï ÏôÑÎ£å');

          // Î∂ÄÎ™® Ïª¥Ìè¨ÎÑåÌä∏Ïóê Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ ÏïåÎ¶º
          onLoginSuccess();

          // URL Ï†ïÎ¶¨
          window.history.replaceState({}, document.title, window.location.pathname);
        } else {
          console.error('OAuth2 Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ÌÜ†ÌÅ∞ Í≤ÄÏ¶ù Ïã§Ìå®');
        }
      })
      .catch(error => {
        console.error('OAuth2 Î°úÍ∑∏Ïù∏ Í≤ÄÏ¶ù Ï§ë Ïò§Î•ò:', error);
      });
    } else {
      console.error('OAuth2 Î°úÍ∑∏Ïù∏ Ïã§Ìå®: success ÌååÎùºÎØ∏ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.');
    }
  }, [onLoginSuccess]);

  return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
        <p className="mt-4 text-gray-600">Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨ Ï§ë...</p>
      </div>
    </div>
  );
};

export default OAuth2Redirect;
