import React, { useState, useEffect } from 'react';
import LoginButton from './components/LoginButton';
import UserProfile from './components/UserProfile';
import OAuth2Redirect from './components/OAuth2Redirect';
import { TokenStorage } from './utils/tokenStorage';
import { ApiService } from './services/api';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [helloMessage, setHelloMessage] = useState('');

  useEffect(() => {
    checkAuthentication();
    fetchHelloMessage();
  }, []);

  const checkAuthentication = async () => {
    try {
      // üîí Ïø†ÌÇ§ Í∏∞Î∞ò ÌÜ†ÌÅ∞ Í≤ÄÏ¶ù API Ìò∏Ï∂ú
      const hasValidToken = await TokenStorage.hasTokens();
      setIsAuthenticated(hasValidToken);
    } catch (error) {
      console.error('Ïù∏Ï¶ù ÌôïÏù∏ Ï§ë Ïò§Î•ò:', error);
      setIsAuthenticated(false);
    }
    setLoading(false);
  };

  const fetchHelloMessage = async () => {
    try {
      const response = await ApiService.hello();
      setHelloMessage(response.data);
    } catch (error) {
      console.error('Hello API Ìò∏Ï∂ú Ïã§Ìå®:', error);
      setHelloMessage('API Ïó∞Í≤∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = async () => {
    try {
      // üîí ÏÑúÎ≤Ñ APIÎ•º ÌÜµÌï¥ Ïø†ÌÇ§ ÏÇ≠Ï†ú
      const success = await TokenStorage.clearTokens();
      if (success) {
        setIsAuthenticated(false);
      }
    } catch (error) {
      console.error('Î°úÍ∑∏ÏïÑÏõÉ Ï§ë Ïò§Î•ò:', error);
      setIsAuthenticated(false); // Ïò§Î•òÍ∞Ä Î∞úÏÉùÌï¥ÎèÑ ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ ÏÉÅÌÉúÎäî ÏóÖÎç∞Ïù¥Ìä∏
    }
  };

  // üîí OAuth2 Î¶¨Îã§Ïù¥Î†âÌä∏ Ï≤òÎ¶¨ (Ïø†ÌÇ§ Í∏∞Î∞ò)
  const urlParams = new URLSearchParams(window.location.search);
  if (urlParams.get('success') === 'true') {
    return <OAuth2Redirect onLoginSuccess={handleLoginSuccess} />;
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex justify-center items-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Î°úÎî© Ï§ë...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Ìó§Îçî */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-gray-900">OAuth2 + JWT Demo</h1>
            </div>
            <div className="flex items-center space-x-4">
              {isAuthenticated ? (
                <span className="text-green-600 font-medium">‚úÖ Ïù∏Ï¶ùÎê®</span>
              ) : (
                <span className="text-red-600 font-medium">‚ùå ÎØ∏Ïù∏Ï¶ù</span>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Î©îÏù∏ Ïª®ÌÖêÏ∏† */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          {/* API Ïó∞Í≤∞ ÏÉÅÌÉú */}
          <div className="bg-white overflow-hidden shadow rounded-lg mb-6">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-2">
                API Ïó∞Í≤∞ ÏÉÅÌÉú
              </h3>
              <div className="text-sm text-gray-600">
                <strong>Hello API ÏùëÎãµ:</strong> {helloMessage}
              </div>
              <button
                onClick={fetchHelloMessage}
                className="mt-3 bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded transition duration-200"
              >
                Îã§Ïãú ÌÖåÏä§Ìä∏
              </button>
            </div>
          </div>

          {/* Ïù∏Ï¶ù ÏÉÅÌÉúÏóê Îî∞Î•∏ Ïª®ÌÖêÏ∏† */}
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              {isAuthenticated ? (
                <div>
                  <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                    ÏÇ¨Ïö©Ïûê ÌîÑÎ°úÌïÑ
                  </h3>
                  <UserProfile onLogout={handleLogout} />
                </div>
              ) : (
                <div className="text-center">
                  <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                    Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§
                  </h3>
                  <p className="text-gray-600 mb-6">
                    Google Í≥ÑÏ†ïÏúºÎ°ú Î°úÍ∑∏Ïù∏ÌïòÏó¨ JWT ÌÜ†ÌÅ∞ Í∏∞Î∞ò Ïù∏Ï¶ùÏùÑ Ï≤¥ÌóòÌï¥Î≥¥ÏÑ∏Ïöî.
                  </p>
                  <LoginButton />
                </div>
              )}
            </div>
          </div>

          {/* üîí Ïø†ÌÇ§ Í∏∞Î∞ò Ïù∏Ï¶ù Ï†ïÎ≥¥ (Í∞úÎ∞úÏö©) */}
          {isAuthenticated && (
            <div className="bg-white overflow-hidden shadow rounded-lg mt-6">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                  Ïù∏Ï¶ù Ï†ïÎ≥¥ (Í∞úÎ∞úÏö©)
                </h3>
                <div className="space-y-2 text-sm">
                  <div className="bg-green-50 border border-green-200 rounded p-3">
                    <strong className="text-green-800">üîí Î≥¥Ïïà Í∞ïÌôî:</strong>
                    <p className="text-green-700 mt-1">
                      JWT ÌÜ†ÌÅ∞Ïù¥ HTTP-Only Ïø†ÌÇ§Ïóê ÏïàÏ†ÑÌïòÍ≤å Ï†ÄÏû•ÎêòÏñ¥ JavaScriptÎ°ú Ï†ëÍ∑ºÌï† Ïàò ÏóÜÏäµÎãàÎã§.
                    </p>
                  </div>
                  <div className="bg-blue-50 border border-blue-200 rounded p-3">
                    <strong className="text-blue-800">‚úÖ XSS Í≥µÍ≤© Î∞©ÏßÄ:</strong>
                    <p className="text-blue-700 mt-1">
                      ÌÜ†ÌÅ∞Ïù¥ Îçî Ïù¥ÏÉÅ localStorageÎÇò URLÏóê ÎÖ∏Ï∂úÎêòÏßÄ ÏïäÏäµÎãàÎã§.
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
